import React, { Component } from 'react';
import './App.css';


class InputCompSmall extends Component {
  constructor(props){
    super(props);
    this.state = {
      currentWord: "",
    }
  }

  updateWord = (event) => {
    this.setState({
      currentWord: event.target.value
    });
  }
  submitWord = () => {
    this.props.submit(this.state.currentWord);
  }

  render() {
    return (
      <div className="inputCompSmall">
        <input class="w3-border w3-hover-red" placeholder="Another Subtitle?" onChange={this.updateWord}></input> 
        <button onClick={this.submitWord}>TITLE</button>
      </div>
    );
  }
}

export default InputCompSmall;
